mapper.select:
select *
from (
select rownum as r, bno, btitle, bwriter, bdate, bhitcount
from (
select bno,
btitle, bwriter, bdate, bhitcount from board order by bno desc
)
where rownum <= #{endNum}
)
where r >= #{startNum}

update.if:
update board set btitle=#btitle}, bcontent=#{bcontent}, bpassword=#{bpassword}, bdate=sysdate,
boriginalfilename=#{boriginalfilename}, bsavedfilename=#{bsavedfilename}, bfilecontent=#{bfilecontent} where
bno=#{bno}
